container.sql:
CREATE TABLE `container` (
  `id` INTEGER PRIMARY KEY AUTOINCREMENT,
  `cpu_limit` VARCHAR DEFAULT (1),
  `container_name` VARCHAR DEFAULT ''
);

container_count.sql:
CREATE TABLE `container_count` (
  `id` INTEGER PRIMARY KEY AUTOINCREMENT,
  `container_count` INTEGER,
  `time` INTEGER
);

hosts.sql:
CREATE TABLE `hosts` (
  `id` INTEGER PRIMARY KEY AUTOINCREMENT,
  `url` VARCHAR,
  `remark` VARCHAR DEFAULT (''),
  `time` INTEGER
);

image_infos.sql:
CREATE TABLE `image_infos` (
  `id` INTEGER PRIMARY KEY AUTOINCREMENT,
  `num` INTEGER,
  `size` INTEGER,
  `time` INTEGER
);
